import React from "react";
import style from "./styles/Pagination.module.css";

const Pagination = ({ dogsPerPage, totalDogs, paginate, currentPage }) => {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalDogs / dogsPerPage); i++) {
    pageNumbers.push(i);
  }
  return (
    <div className={style.paginatedContainer}>
      {pageNumbers &&
        pageNumbers.map((num) => {
          return (
            <button
              key={num}
              className={
                currentPage === num ? style.buttonActive : style.numberButton
              }
              onClick={() => paginate(num)}
            >
              {num}
            </button>
          );
        })}
    </div>
    // <nav>
    //   <div className={style.Container}>
    //     {pageNumbers && pageNumbers.map((number) => (
    //       <div key={number} className={style.pageNumber}>
    //         <button className={style.currentNumber} onClick={() => paginate(number)}>{number}</button>
    //       </div>
    //     ))}
    //   </div>
    // </nav>
  );
};

export default Pagination;
