import { GET_DOGS, FILTER_API_VS_CREATED, ORDER_BY_NAME } from "./actions";

const initialState = {
  dogs: [],
  temperaments: [],
  allDogs: [],
};

function reducer(state = initialState, { type, payload }) {
  switch (type) {
    case GET_DOGS:
      return {
        ...state,
        dogs: payload,
        allDogs: payload,
      };

    case FILTER_API_VS_CREATED:
      const allDogs = state.allDogs;
      const filtered =
        payload === "api"
          ? allDogs.filter((dog) => dog.id < 300)
          : allDogs.filter((dog) => dog.id.length > 3);
      return {
        ...state,
        dogs: payload === "All" ? state.allDogs : filtered,
      };
    case ORDER_BY_NAME:
      const orderedArray =
        payload === "ascend"
          ? state.dogs.sort(function (a, b) {
              return a.name - b.name;
            })
          : state.dogs.sort(function (a, b) {
              return b.name - a.name;
            });
      return {
        ...state,
        dogs: orderedArray,
      };

    // case FILTER_API_VS_CREATED:
    //   const allDogs2 = state.allDogs;
    //   const filtered =
    //     payload === "All"
    //       ? allDogs2
    //       : allDogs2.filter((dog) => dog.origin === payload);
    //   return {
    //     ...state,
    //     dogs: filtered,
    //   };
    default:
      return state;
  }
}

export default reducer;
