import React, { useState } from "react";
import style from "./styles/Create.module.css";

export function validate(input) {
  let errors = {};
  if (!input.name) {
    errors.name = "Name is required";
  } else if (!/\S+@\S+\.\S+/.test(input.name)) {
    errors.name = "Name is invalid";
  }
  if (!input.height) {
    errors.height = "Height is required";
  } else if (!/(?=.*[0-9])/.test(input.height)) {
    errors.height = "Height is invalid";
  }
  if (!input.weight) {
    errors.weight = "Weight is required";
  } else if (!/(?=.*[0-9])/.test(input.weight)) {
    errors.weight = "Weight is invalid";
  }
  if (!input.lifeSpan) {
    errors.lifeSpan = "Life Span is required";
  } else if (!/(?=.*[0-9])/.test(input.lifeSpan)) {
    errors.lifeSpan = "Life Span is invalid";
  }
  if (!input.temperament) {
    errors.temperament = "Temperament is required";
  } else if (!/(?=.*[0-9])/.test(input.lifeSpan)) {
    errors.temperament = "temperament is invalid";
  }
  return errors;
}

const Create = () => {
  const [input, setInput] = useState({
    name: "",
    height: "",
    weight: "",
    lifeSpan: "",
    temperament: "",
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    setInput((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
    setErrors(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
  };

  function handleSubmit(e) {
    e.preventDefault();
    console.log(input);
  }

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name:</label>
        <input
          className={errors.username && style.danger}
          type="text"
          name="name"
          onChange={handleInputChange}
          value={input.name}
        />
        {errors.username && <p className={style.danger}>{errors.username}</p>}
      </div>
      <div>
        <label>Height:</label>
        <input
          className={errors.password && style.danger}
          type="text"
          name="height"
          onChange={handleInputChange}
          value={input.height}
        />
        {errors.password && <p className={style.danger}>{errors.password}</p>}
      </div>
      <div>
        <label>Weight:</label>
        <input
          className={errors.password && style.danger}
          type="text"
          name="weight"
          onChange={handleInputChange}
          value={input.weight}
        />
        {errors.password && <p className={style.danger}>{errors.password}</p>}
      </div>
      <div>
        <label>Life Span:</label>
        <input
          className={errors.password && style.danger}
          type="text"
          name="lifeSpan"
          onChange={handleInputChange}
          value={input.lifeSpan}
        />
        {errors.password && <p className={style.danger}>{errors.password}</p>}
      </div>
      <div>
        <label>Temperament:</label>
        <input
          className={errors.password && style.danger}
          type="text"
          name="temperament"
          onChange={handleInputChange}
          value={input.temperament}
        />
        {errors.password && <p className={style.danger}>{errors.password}</p>}
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Create;
